/*
** ===================================================================
**     Method      :  SPI_2_Interrupt (component SynchroMaster)
**
**     Description :
**         The method services the interrupt of the selected peripheral(s)
**         and eventually invokes event(s) of the component.
**         This method is internal. It is used by Processor Expert only.
** ===================================================================
*/
#define ON_ERROR       0x01U
#define ON_FULL_RX     0x02U
#define ON_RX_CHAR     0x04U
#define ON_FREE_TX     0x08U
#define ON_TX_CHAR     0x10U
#define ON_RX_CHAR_EXT 0x20U
#pragma CODE_SEG __NEAR_SEG NON_BANKED
ISR(SPI_2_Interrupt)
{
  SPI_2_TComData Data = 0U;            /* Temporary variable for data */
  byte Flags = 0U;                     /* Temporary variable for flags */
  byte Status;                         /* Temporary variable for flags */

  Status = SPI0SR;                     /* Read the device error register */
  Data = SPI0DRL;                      /* Read data from receiver */
  if(SPI_2_InpLen < SPI_2_INP_BUF_SIZE) { /* Is number of bytes in the receive buffer lower than size of buffer? */
    SPI_2_InpLen++;                    /* Increse number of chars in the receive buffer */
    *(InpPtrW++) = Data;               /* Save received char to the receive buffer */
    /*lint -save  -e946 Disable MISRA rule (17.2) checking. */
    if(InpPtrW >= (InpBuffer + SPI_2_INP_BUF_SIZE)) { /* Is the pointer out of the receive buffer? */
      InpPtrW = InpBuffer;             /* Set pointer on the first item into the receive buffer */
    }
    /*lint -restore Enable MISRA rule (17.2) checking. */
    if(SPI_2_InpLen == SPI_2_INP_BUF_SIZE) { /* Is number of bytes in the receive buffer equal to the size of buffer? */
      Flags |= ON_FULL_RX;             /* Set flag "OnFullRxBuf" */
    }
  } else {
    SerFlag |= FULL_RX;                /* Set flag "full RX buffer" */
    Flags |= ON_ERROR;                 /* Set the OnError flag */
    ErrFlag |= FULL_RX;                /* Set the error flag for the GetError method */
  }
  SPI_2_OutLen--;                      /* Decrease number of chars in the transmit buffer */
  OutPtrR++;                           /* Increment pointer */
  /*lint -save  -e946 Disable MISRA rule (17.2) checking. */
  if (OutPtrR >= (OutBuffer + SPI_2_OUT_BUF_SIZE)) { /* Is the pointer out of the transmit buffer? */
    OutPtrR = OutBuffer;               /* Set pointer on the first item into the transmit buffer */
  }
  /*lint -restore Enable MISRA rule (17.2) checking. */
  if (SPI_2_OutLen) {                  /* Is number of bytes in the transmit buffer greather then 0? */
    SPI0DRL = *(OutPtrR);              /* Store char to transmitter register */
  } else {
    SerFlag &= (byte)(~(byte)(RUNINT_FROM_TX|FULL_TX)); /* Clear "running int from TX" and "full TX buff" flags */
    if (!OnFreeTxBuf_semaphore) {      /* Is the OnFreeTXBuf flag blocked ?*/
      Flags |= ON_FREE_TX;             /* If not, set the OnFreeTXBuf flag */
    }
  }
  if(Flags & ON_ERROR) {               /* Is any error flag set? */
    __DI();
    SPI_2_OnError();                   /* If yes then invoke user event */
  } else {
    if(Flags & ON_FULL_RX) {           /* Is OnFullRxBuf flag set? */
      __DI();
      SPI_2_OnFullRxBuf();             /* If yes then invoke user event */
    }
  }
  if (Flags & ON_FREE_TX) {            /* Is flag "OnFreeTxBuf" set? */
    __DI();
    SPI_2_OnFreeTxBuf();               /* If yes then invoke user event */
  }
}

